import kotlin.math.abs

/**
 * Main -------------------------------------------------------------------
 */
fun main() {

    fun Int.toIdentity(): Int {
        if (this == 0) {
            return 0
        }

        if (this < 1) {
            return -1
        } else {
            return 1
        }
    }

    fun getHeadPositions(input: List<String>): List<Pair<Int, Int>> {
        val positionList = mutableListOf<Pair<Int, Int>>()
        var headR = 0
        var headC = 0

        input.forEach {
            val token = it.split(" ")
            var step = token[1].toInt()

            System.err.println(">>$it")
            while (step > 0) {
                //System.err.println("STEPS: $step")
                step-- //we take a step

                when (token[0]) {
                    "R" -> {
                        headC++
                    }

                    "L" -> {
                        headC--
                    }

                    "U" -> {
                        headR++
                    }

                    "D" -> {
                        headR--
                    }
                }
                positionList.add(Pair(headR, headC))
            }
        }

        return positionList
    }

    fun getTailPositions(headPositions: List<Pair<Int, Int>>): List<Pair<Int, Int>> {
        val tailPositions = mutableListOf<Pair<Int, Int>>()

        var tailR = 0
        var tailC = 0

        headPositions.forEach {

            val headR = it.first
            val headC = it.second

            val diffC = headC - tailC
            val diffR = headR - tailR
            val deltaC = abs(diffC)
            val deltaR = abs(diffR)

            if (deltaC == 2 || deltaR == 2) {
                tailC += diffC.toIdentity()
                tailR += diffR.toIdentity()
            }

            //System.err.println("H:$headR, $headC --- T:$tailR, $tailC")

            tailPositions.add(Pair(tailR, tailC))

        }

        return tailPositions
    }

    fun part1(input: List<String>): Int {
        val headPositions = getHeadPositions(input)
        val tailPositions = getTailPositions(headPositions)

        return tailPositions.toSet().size
    }

    fun part2(input: List<String>): Int {
        val headPositions = getHeadPositions(input)
        var tailPositions = getTailPositions(headPositions) //tail 1

        for (i in 2 .. 9) { //tails 2 .. 9
            System.err.println("$i")
            tailPositions = getTailPositions(tailPositions)
        }

        return tailPositions.toSet().size
    }


    val input = readInput(9)
    println(part1(input))
    println(part2(input))
}

import org.junit.jupiter.api.Test
import kotlin.test.assertEquals


class Day21 {

    @Test
    fun `test demo input`() {
        val input =
            "mxmxvkd kfcds sqjhc nhms (contains dairy, fish)\n" +
                    "trh fvjkl sbzzf mxmxvkd (contains dairy)\n" +
                    "sqjhc fvjkl (contains soy)\n" +
                    "sqjhc mxmxvkd sbzzf (contains fish)"

        println(countTimesNonAllergenicItemAppears(input))

    }

    @Test
    fun `test real input`() {
        val input = readFileAsString("/Users/fredrikfolkesson/git/advent-of-code/inputs/input-day21.txt")

        println(countTimesNonAllergenicItemAppears(input))


    }

    private fun countTimesNonAllergenicItemAppears(input: String): Int {

        val allIngredients = input.lines().map { parseFood(it) }.flatMap { it.values.first() }.toSet()
        val allergenMap = findAllergenMap(input)
        val nonAllergenics = allIngredients.filter { it !in allergenMap.values }.toSet()


        val allIngredientsList = input
            .lines()
            .map { parseFood(it) }
            .map { it.values.first() }

        val allIngredientsCount = mutableMapOf<String, Int>()
        allIngredientsList.forEach {
            it.forEach { ingredient ->
                allIngredientsCount[ingredient] = allIngredientsCount.getOrDefault(ingredient, 0) + 1
            }
        }
        return allIngredientsCount.filter { it.key in nonAllergenics }.values.sum()
    }


    @Test
    fun `test demo input part 2`() {
        val input =
            "mxmxvkd kfcds sqjhc nhms (contains dairy, fish)\n" +
                    "trh fvjkl sbzzf mxmxvkd (contains dairy)\n" +
                    "sqjhc fvjkl (contains soy)\n" +
                    "sqjhc mxmxvkd sbzzf (contains fish)"


        val allergenMap = findAllergenMap(input)
        println(allergenMap.toSortedMap().values.joinToString(","))
    }

    @Test
    fun `test real input part 2`() {
        val input = readFileAsString("/Users/fredrikfolkesson/git/advent-of-code/inputs/input-day21.txt")


        val allergenMap = findAllergenMap(input)
        println(allergenMap.toSortedMap().values.joinToString(","))
    }


    private fun findAllergenMap(foods: String): MutableMap<String, String> {

        val foundPossibleAllergens = mutableMapOf<String, MutableSet<String>>()
        //First remove intersections
        foods
            .lines()
            .map { parseFood(it) }
            .forEach {
                it.keys.forEach { key ->
                    if (key in foundPossibleAllergens) {
                        val currentPossibleAllergens = foundPossibleAllergens[key]!!
                        foundPossibleAllergens[key] = currentPossibleAllergens.intersect(it.values.first()).toMutableSet()
                    } else {
                        //They are all the same
                        foundPossibleAllergens[key] = it.values.first().toMutableSet()
                    }
                }
            }

        val ingredientToAllergen = mutableMapOf<String,String>()
        while (foundPossibleAllergens.values.any { it.isNotEmpty() }) {

            val entryThatIsAnAllergen = foundPossibleAllergens.entries.first { it.value.size == 1 }
            val onlyOneIngredientForAllergen = entryThatIsAnAllergen.value.toList().first()

            ingredientToAllergen.put(entryThatIsAnAllergen.key,onlyOneIngredientForAllergen)

            foundPossibleAllergens.values.map {
                it.remove(onlyOneIngredientForAllergen)
            }

        }

        return ingredientToAllergen

    }

    @Test
    fun `test parse foodItem`() {

        assertEquals(
            mapOf(
                "fish" to setOf("mxmxvkd", "kfcds", "sqjhc", "nhms"),
                "dairy" to setOf("mxmxvkd", "kfcds", "sqjhc", "nhms")
            ), parseFood("mxmxvkd kfcds sqjhc nhms (contains dairy, fish)")
        )
        assertEquals(
            mapOf("dairy" to setOf("trh", "fvjkl", "sbzzf", "mxmxvkd")),
            parseFood("trh fvjkl sbzzf mxmxvkd (contains dairy)")
        )

    }

    private fun parseFood(input: String): Map<String, Set<String>> {
        val ingredients = input.split(" (contains ")[0].split(" ").toSet()
        return input.split(" (contains ")[1].replace(")", "").split(", ").map { it to ingredients }.toMap()
    }


}

import kotlin.math.floor
import kotlin.math.sqrt
import kotlin.system.measureNanoTime

/**
 * Summation of primes
 *
 * The sum of the primes below 10 is 2 + 3 + 5 + 7 = 17.
 * Find the sum of all the primes below two million.
 *
 * https://projecteuler.net/problem=10
 */

fun main() {
    println(sumOfPrimes(10))
    println(sumOfPrimesSieve(10))
    val time1 = measureNanoTime { println(sumOfPrimes(2_000_000)) }
    val time2 = measureNanoTime { println(sumOfPrimesSieve(2_000_000)) }

    println(time1)
    println(time2)
}

private fun sumOfPrimes(n: Long): Long {
    if (n < 2) return 0
    return (3..n step 2).asSequence()
        .filter(::isPrime)
        .sum() + 2
}

private fun sumOfPrimesSieve(n: Long): Long {
    return primes(n).sumOf(Int::toLong)
}

// The sieve of Eratosthenes optimized
fun primes(n: Long): List<Int> {
    val sieveBound = ((n - 1) / 2).toInt()
    val sieve = Array(sieveBound) { true }
    val crosslimit = (floor(sqrt(n.toDouble())).toInt() - 1) / 2

    for (i in 1..crosslimit) {
        if (!sieve[i-1]) continue
        for (j in (2 * i * (i + 1)) .. sieveBound step 2 * i + 1) {
            sieve[j-1] = false
        }
    }

    return listOf(2) + sieve.mapIndexedNotNull { i, isPrime -> if (isPrime) 2 * (i + 1) + 1 else null }
}

package `2022`

import readInput
import kotlin.math.abs

fun main() {

    fun findDistressedBeacon(sensorToDistance: List<Day15Input>, maxSize: Int): YPoint {
        (0..maxSize).forEach { y ->
            val ranges = sensorToDistance.toReverseRanges(y, maxSize)
            if (ranges.isNotEmpty()) {
                return ranges.first().start
            }
        }
        error("Can't find distressed beacon")
    }

    fun readInput(input: List<String>): List<Day15Input> {
        val sensorToDistance = input.map { s ->
            val (sensor, beacon) = "(x=|y=)(-?\\d+)"
                .toRegex().findAll(s)
                .map { it.groups[2]!!.value.toInt() }
                .chunked(2)
                .map { (x, y) -> YPoint(x, y) }
                .toList()
            val distance = sensor.dist(beacon)
            Day15Input(sensor, distance, beacon)
        }
        return sensorToDistance
    }

    fun part1(input: List<String>, row: Int): Int {
        val sensorToBeacon = readInput(input)
        val sensors = sensorToBeacon.map { it.sensor }.toSet()
        val beacons = sensorToBeacon.map { it.beacon }.toSet()
        val ranges = sensorToBeacon.toRanges(row)
        val minX = ranges.map { it.start.x }.min()
        val maxX = ranges.map { it.endInclusive.x }.max()

        return (minX..maxX).asSequence().filter { x ->
            val point = YPoint(x, row)
            !sensors.contains(point) && !beacons.contains(point)
        }.count()
    }

    fun part2(input: List<String>, maxSize: Int): Long {
        val sensorToDistance = readInput(input)
        val (x, y) = findDistressedBeacon(sensorToDistance, maxSize)
        return x * 4000000L + y
    }

    // test if implementation meets criteria from the description, like:
    val testInput = readInput("Day15_test")
    println(part1(testInput, 10))
    println(part2(testInput, 20))
    check(part1(testInput, 10) == 26)
    check(part2(testInput, 20) == 56000011L)

    val input = readInput("Day15")
    println(part1(input, 2000000))
    println(part2(input, 4000000))
}

private fun YPoint.dist(other: YPoint): Int {
    return abs(x - other.x) + abs(y - other.y)
}

private data class Day15Input(val sensor: YPoint, val distance: Int, val beacon: YPoint)

private data class YPoint(val x: Int, val y: Int): Comparable<YPoint> {
    override fun compareTo(other: YPoint): Int {
        return x - other.x
    }
}

private fun YPoint.prev(): YPoint {
    return YPoint(x - 1, y)
}

private fun YPoint.next(): YPoint {
    return YPoint(x + 1, y)
}

private fun List<Day15Input>.toRanges(row: Int): List<ClosedRange<YPoint>> {
    return mapNotNull { (sensor, distance) ->
        val delta = distance - abs(sensor.y - row)
        if (delta >= 0) {
            YPoint(sensor.x - delta, row)..YPoint(sensor.x + delta, row )
        } else {
            null
        }
    }
}

private fun List<Day15Input>.toReverseRanges(row: Int, maxSize: Int): List<ClosedRange<YPoint>> {
    val ranges = toRanges(row)
    return ranges.fold(listOf(YPoint(0, row)..YPoint(maxSize, row))) { acc, range ->
        acc.subtract(range)
    }
}

private fun List<ClosedRange<YPoint>>.subtract(range: ClosedRange<YPoint>): List<ClosedRange<YPoint>> {
    return filter {
        !(range.contains(it.start) && range.contains(it.endInclusive))
    }.flatMap {
        if (range.contains(it.start)) {
            listOf(range.endInclusive.next()..it.endInclusive)
        } else if (range.contains(it.endInclusive)) {
            listOf(it.start..range.start.prev())
        } else if (it.contains(range.start)) {
            listOf(
                it.start..range.start.prev(),
                range.endInclusive.next()..it.endInclusive
            )
        } else {
            listOf(it)
        }
    }
}

package com.hj.leetcode.kotlin.problem2251

/**
 * LeetCode page: [2251. Number of Flowers in Full Bloom](https://leetcode.com/problems/number-of-flowers-in-full-bloom/);
 */
class Solution {
    /* Complexity:
     * Time O((N+M)LogN) and Space O(N) where N is the size of flowers and
     * M is the size of people;
     */
    fun fullBloomFlowers(flowers: Array<IntArray>, people: IntArray): IntArray {
        val sortedStarts =
            IntArray(flowers.size) { flowers[it][0] }.apply { sort() }

        val sortedEnds =
            IntArray(flowers.size) { flowers[it][1] }.apply { sort() }

        return IntArray(people.size) { index ->
            numFlowersInFullBloom(people[index], sortedStarts, sortedEnds)
        }
    }

    private fun numFlowersInFullBloom(
        time: Int,
        sortedStarts: IntArray,
        sortedEnds: IntArray
    ): Int {
        val numStarted = leftInsertionIndex(time + 1, sortedStarts)
        val numEnded = leftInsertionIndex(time, sortedEnds)
        return numStarted - numEnded
    }

    /**
     * Return the leftmost index to insert the [target] while keeping
     * the [sorted] sorted.
     */
    private fun leftInsertionIndex(target: Int, sorted: IntArray): Int {
        if (sorted.isEmpty() || target <= sorted[0]) {
            return 0
        }
        if (sorted.last() < target) {
            return sorted.size
        }

        var left = 0
        var right = sorted.lastIndex

        while (left < right) {
            val mid = (left + right) ushr 1

            if (sorted[mid] < target) {
                left = mid + 1
            } else {
                right = mid
            }
        }
        return left
    }
}

package nl.kelpin.fleur.advent2018

typealias GuardID = Int
typealias Minute = Int

class Day04(input: List<String>) {
    data class Nap(val guard: GuardID, val minutes: IntRange)

    data class State(val guard: GuardID? = null, val napStarted: Int? = null, val naps: List<Nap> = listOf()) {
        fun beginShift(newGuard: GuardID) = copy(guard = newGuard, napStarted = null)
        fun fallAsleep(minutes: Int) = copy(napStarted = minutes)
        fun wakeUp(minutes: Int) = copy(napStarted = null, naps = naps + Nap(guard!!, napStarted!! until minutes))
    }

    companion object {
        private val beginShiftRE = Regex(""".*Guard #(\d+) begins shift""")
        private val fallAsleepRE = Regex(""".*00:(\d{2})] falls asleep""")
        private val wakesUpRE = Regex(""".*00:(\d{2})] wakes up""")
        private fun MatchResult.intMatch(): Int = destructured.let{ (firstResult) -> firstResult.toInt() }

        fun processLine(status: State, line: String): State = when {
            line.matches(beginShiftRE) -> beginShiftRE.find(line)!!.intMatch().run(status::beginShift)
            line.matches(fallAsleepRE) -> fallAsleepRE.find(line)!!.intMatch().run(status::fallAsleep)
            line.matches(wakesUpRE) -> wakesUpRE.find(line)!!.intMatch().run(status::wakeUp)
            else -> throw IllegalArgumentException("Failed to parse: $line")
        }
    }

    private val guardNaps: Map<GuardID, List<Minute>> = input
            .sorted()
            .fold(State(), ::processLine).naps
            .groupBy { it.guard }
            .mapValues { it.value.flatMap { it.minutes } }

    fun part1(): Int = guardNaps
            .maxBy { it.value.size }!!
            .let { (guardID: GuardID, minutes: List<Minute>) -> guardID * minutes.mostFrequent().element }

    fun part2(): Int = guardNaps
            .mapValues { it.value.mostFrequent() }
            .maxBy { it.value.occurrence }!!
            .let { (guardID: GuardID, mostFrequent: Frequency<Minute>) -> guardID * mostFrequent.element }
}

package g2501_2600.s2503_maximum_number_of_points_from_grid_queries

// #Hard #Array #Sorting #Breadth_First_Search #Heap_Priority_Queue #Union_Find
// #2023_07_04_Time_581_ms_(100.00%)_Space_62.6_MB_(100.00%)

import java.util.ArrayDeque
import java.util.Arrays
import java.util.PriorityQueue
import java.util.Queue

class Solution {
    private val dirs = arrayOf(intArrayOf(-1, 0), intArrayOf(1, 0), intArrayOf(0, -1), intArrayOf(0, 1))

    fun maxPoints(grid: Array<IntArray>, queries: IntArray): IntArray {
        val r = grid.size
        val c = grid[0].size
        val res = IntArray(queries.size)
        val index = arrayOfNulls<Int>(queries.size)
        for (i in queries.indices) {
            index[i] = i
        }
        Arrays.sort(index, { o: Int?, m: Int? -> queries[o!!].compareTo(queries[m!!]) })
        val q1: Queue<IntArray> = ArrayDeque()
        val q2 = PriorityQueue({ a: IntArray, b: IntArray -> a[2].compareTo(b[2]) })
        q2.offer(intArrayOf(0, 0, grid[0][0]))
        val visited = Array(r) { BooleanArray(c) }
        var count = 0
        visited[0][0] = true
        for (i in queries.indices) {
            val currLimit = queries[index[i]!!]
            while (q2.isNotEmpty() && q2.peek()[2] < currLimit) {
                q1.offer(q2.poll())
            }
            while (q1.isNotEmpty()) {
                val curr = q1.poll()
                count++
                for (dir in dirs) {
                    val x = dir[0] + curr[0]
                    val y = dir[1] + curr[1]
                    if (x < 0 || y < 0 || x >= r || y >= c || visited[x][y]) {
                        continue
                    }
                    if (!visited[x][y] && grid[x][y] < currLimit) {
                        q1.offer(intArrayOf(x, y, grid[x][y]))
                    } else {
                        q2.offer(intArrayOf(x, y, grid[x][y]))
                    }
                    visited[x][y] = true
                }
            }
            res[index[i]!!] = count
        }
        return res
    }
}

package eu.janvdb.aoc2023.day19

import eu.janvdb.aocutil.kotlin.readGroupedLines

//const val FILENAME = "input19-test.txt"
const val FILENAME = "input19.txt"

val VARIABLES = listOf("x", "m", "a", "s")

const val MIN_VALUE = 1
const val MAX_VALUE = 4000

fun main() {
    val lines = readGroupedLines(2023, FILENAME)
    val rules = Rules.parse(lines[0])
    val parts = lines[1].map(Part::parse)

    part1(parts, rules)

    part2(rules)
}

private fun part1(parts: List<Part>, rules: Rules) {
    val acceptedParts = parts.filter(rules::evaluate)
    println(acceptedParts.sumOf { it.rating })
}

private fun part2(rules: Rules) {
    val ranges = VARIABLES.associateWith { MIN_VALUE..MAX_VALUE }
    val result = rules.countAccepted("in", ranges)
    println(result)
}

data class Rules(val rules: Map<String, Rule>) {
    fun evaluate(part: Part): Boolean {
        var current = "in"
        while (!current.isAccepted() && !current.isRejected()) {
            val rule = rules[current]!!
            current = rule.evaluate(part)
        }

        return current.isAccepted()
    }

    fun countAccepted(outcome: String, ranges: Map<String, IntRange>, index: Int = 0): Long {
        val spaces = (0..<index).joinToString("", "", "+-") { "| " }
        println("$spaces$ranges -> $outcome")

        if (outcome.isAccepted())
            return 1L * ranges.values.fold(1L) { acc, range -> acc * (range.last - range.first + 1) }
        if (outcome.isRejected())
            return 0L
        if (ranges.values.any { it.last < it.first })
            return 0L

        val rule = rules[outcome]!!

        var currentRanges = ranges
        var sum = 0L
        rule.conditions.forEach { condition ->
            val currentRange = currentRanges[condition.variable]!!
            val newRange1 =
                if (condition.operation == Operation.LT) currentRange.first..<condition.value else condition.value + 1..currentRange.last
            val newRange2 =
                if (condition.operation == Operation.LT) condition.value..currentRange.last else currentRange.first..condition.value

            val newRanges = currentRanges + Pair(condition.variable, newRange1)
            sum += countAccepted(condition.outcome, newRanges, index + 1)

            currentRanges = currentRanges + Pair(condition.variable, newRange2)
        }

        sum += countAccepted(rule.defaultOutcome, currentRanges, index + 1)

        return sum
    }

    companion object {
        fun parse(lines: List<String>): Rules {
            val rules = lines.map(Rule::parse).associateBy { it.name }
            return Rules(rules)
        }
    }
}


data class Rule(val name: String, val conditions: List<Condition>, val defaultOutcome: String) {
    fun evaluate(part: Part): String {
        return conditions.find { it.matches(part) }?.outcome ?: defaultOutcome
    }

    companion object {
        fun parse(line: String): Rule {
            val parts = line.split("{")
            val name = parts[0]
            val parts2 = parts[1].substring(0, parts[1].length - 1).split(",")
            val conditions = parts2.subList(0, parts2.size - 1).map(Condition::parse)
            val defaultOutcome = parts2.last()
            return Rule(name, conditions, defaultOutcome)
        }
    }
}

data class Condition(val variable: String, val operation: Operation, val value: Int, val outcome: String) {
    fun matches(part: Part): Boolean {
        val partValue = part.values[variable]!!
        return operation.evaluate(partValue, value)
    }

    companion object {
        private val REGEX = Regex("(\\w+)([<>])(\\d+):(\\w+)")
        fun parse(value: String): Condition {
            val matchResult = REGEX.matchEntire(value)!!
            return Condition(
                matchResult.groupValues[1],
                Operation.parse(matchResult.groupValues[2]),
                matchResult.groupValues[3].toInt(),
                matchResult.groupValues[4]
            )
        }
    }
}

enum class Operation(val text: String) {
    GT(">"), LT("<");

    fun evaluate(x: Int, y: Int) = when (this) {
        LT -> x < y
        GT -> x > y
    }

    companion object {
        fun parse(value: String) = entries.find { it.text == value }!!
    }
}

fun String.isAccepted() = equals("A")
fun String.isRejected() = equals("R")

data class Part(val values: Map<String, Int>) {
    val rating = VARIABLES.sumOf { values.getOrDefault(it, 0) }

    companion object {
        private val REGEX = Regex("(\\w+)=(\\d+)")
        fun parse(value: String): Part {
            val values = REGEX.findAll(value).associate { Pair(it.groupValues[1], it.groupValues[2].toInt()) }
            return Part(values)
        }
    }
}

package com.jacobhyphenated.advent2022.day13

import com.jacobhyphenated.advent2022.Day

/**
 * Day 13: Distress Signal
 *
 * Packets are coming in out of order.
 * Packets come in pairs (one on each line of the input).
 *
 * A packet consists of a number or list of packets, ex:
 *      [[1],[2,3,4]] turns into Packet[Packet[Packet[1]], Packet[Packet[2],Packet[3],Packet[4]]]
 *      [[1],4] turns into Packet[Packet[Packet[1]], Packet[4]]
 *
 */
class Day13: Day<List<Pair<Packet, Packet>>> {
    override fun getInput(): List<Pair<Packet, Packet>> {
        return parseInput(readInputFile("day13"))
    }

    /**
     * Observe each pair of packets. Find the index (1 based) in the list of
     * all the packets that are in the correct order.
     * Return the sum of those indexes
     */
    override fun part1(input: List<Pair<Packet, Packet>>): Int {
        return input.mapIndexed { index, (lhs, rhs) ->
            if (lhs <= rhs) { index + 1 } else { 0 }
        }.sum()
    }

    /**
     * Take all packets and put them in a single list. Add two marker packets: [[2]] and [[6]]
     * Put the packets in the correct order and find the (1 based) index of the marker packets.
     * Return the product of both marker indexes
     */
    override fun part2(input: List<Pair<Packet, Packet>>): Any {
        val (marker1, _) = parsePacket("[[2]]")
        val (marker2, _) = parsePacket("[[6]]")
        val allPackets = input.flatMap { listOf(it.first, it.second) } + listOf(marker1, marker2)
        val sortedPackets = allPackets.sorted()
        return (sortedPackets.indexOf(marker1) + 1) * (sortedPackets.indexOf(marker2) + 1)
    }

    fun parseInput(input: String): List<Pair<Packet, Packet>> {
        return input.split("\n\n").map { pairs ->
            val (lhs, rhs) = pairs.lines().map { parsePacket(it).first }
            Pair(lhs, rhs)
        }
    }

    /**
     * Recursive function to parse the packet out of the string.
     * Nested [] get their own recursive call.
     *
     * @param packetString The entire packet string
     * @param index the index to start the parsing at (defaults to 1 - all packets start with '[')
     * @return A pair tuple consisting of the parsed packet
     * and the index of the lasted visited location in the overall packetString
     */
    private fun parsePacket(packetString: String, index: Int = 1): Pair<Packet, Int> {
        var buffer = ""
        var i = index
        val packets = mutableListOf<Packet>()
        // when we hit the close bracket, this parse function is done
        while (packetString[i] != ']') {
            if (packetString[i] == '[') {
                // we are opening a new sub packet - recursively call this method
                val (p, newIndex) = parsePacket(packetString, i+1)
                packets.add(p)
                i = newIndex
                continue
            }
            if (packetString[i] == ',') {
                // On a comma, move to the next item in the list (clear the buffer if necessary)
                if (buffer.isNotEmpty()) {
                    packets.add(Packet(buffer.toInt(), null))
                    buffer = ""
                }
                i++
                continue
            }
            // Numbers are stored in the buffer (as multi digit numbers are valid)
            buffer += packetString[i]
            i++
        }
        if (buffer.isNotEmpty()) {
            packets.add(Packet(buffer.toInt(), null))
        }
        return Pair(Packet(null, packets), i + 1)
    }
}

class Packet (
    private val value: Int?,
    private val packets: List<Packet>?
) : Comparable<Packet> {

    /**
     * To check order, look from left to right in each packet
     *      If the value is a number on both, the left should be less than the right
     *      If one of the two packets has a number, convert that number to a single value list
     *      If both are lists, go from left to right comparing each value in the list (left-hand side should be smaller)
     *      - if the left-hand side runs out of values first, they are in the correct order
     *      - if the right-hand side runs out of values first, they are out of order
     */
    override fun compareTo(other: Packet): Int {
        if (this.value != null && other.value != null) {
            return this.value.compareTo(other.value)
        }
        val listA = this.packets ?: listOf(Packet(this.value!!, null))
        val listB = other.packets ?: listOf(Packet(other.value!!, null))
        for (i in listA.indices) {
            if (i >= listB.size) { return 1 }
            val cmp = listA[i].compareTo(listB[i])
            if (cmp != 0) { return cmp }
        }
        if (listA.size < listB.size) {
            return - 1
        }
        return 0
    }

    /**
     * We could probably cheat and just do reference equivalence for this problem
     * But here is a real equals method implementation
     */
    override fun equals(other: Any?): Boolean {
        if (this === other) { return true }
        if (other !is Packet) { return false }
        if (other.value != null && this.value != null && this.value == other.value) {
            return true
        }
        if (other.packets != null && this.packets != null){
            if (other.packets.size != this.packets.size) { return false }
            return other.packets == this.packets
        }
        return false
    }

    // The IDE generated this one (It's always recommended to have hashCode if you have equals)
    override fun hashCode(): Int {
        val result = value ?: 0
        return 31 * result + (packets?.hashCode() ?: 0)
    }

}

package net.zomis.games.dsl

data class Player(var chips: Money = Money(), val owned: MutableSet<Card> = mutableSetOf(),
                  val reserved: MutableSet<Card> = mutableSetOf()) {
    fun total(): Money = this.discounts() + this.chips

    fun canBuy(card: Card): Boolean = this.total().has(card.costs)

    fun buy(card: Card) {
        val actualCost = card.costs.minus(this.discounts())
        this.chips -= actualCost
        this.owned.add(card)
    }

    fun discounts(): Money {
        return this.owned.map { it.discounts }.fold(Money()) { acc, money -> acc + money }
    }

}

data class Card(val level: Int, val discounts: Money, val costs: Money, val points: Int) {
    // Possible to support multiple discounts on the same card. Because why not!
    constructor(level: Int, discount: MoneyType, costs: Money, points: Int):
        this(level, Money(discount to 1), costs, points)
}

fun <K, V> Map<K, V>.mergeWith(other: Map<K, V>, merger: (V?, V?) -> V): Map<K, V> {
    return (this.keys + other.keys).associateWith {
        merger(this[it], other[it])
    }
}

enum class MoneyType {
    BLACK, WHITE, RED, BLUE, GREEN;

    fun toMoney(count: Int): Money {
        return Money(mutableMapOf(this to count))
    }
}
data class Money(val moneys: MutableMap<MoneyType, Int> = mutableMapOf()) {
    // TODO: Add wildcards as a separate field
    constructor(vararg money: Pair<MoneyType, Int>) : this(mutableMapOf<MoneyType, Int>(*money))

    operator fun plus(other: Money): Money {
        val result = moneys.mergeWith(other.moneys) {a, b -> (a ?: 0) + (b ?: 0)}
        return Money(result.toMutableMap())
    }

    fun has(costs: Money): Boolean = costs.moneys.entries.all { it.value <= this.moneys[it.key] ?: 0 }

    operator fun minus(other: Money): Money {
        val result = moneys.mergeWith(other.moneys) {a, b -> (a ?: 0) - (b ?: 0)}.mapValues {
            if (it.value < 0) 0 else it.value
        }
        return Money(result.toMutableMap())
    }
}

data class MoneyChoice(val moneys: List<MoneyType>) {
    fun toMoney(): Money {
        return Money(moneys.groupBy { it }.mapValues { it.value.size }.toMutableMap())
    }
}

