package co.csadev.advent2020

class Day03(private val input: List<String>) {

    private val width = input.first().length

    fun solvePart1(): Int = checkTrees(3 to 1)

    fun solvePart2(): Long = listOf(1 to 1, 3 to 1, 5 to 1, 7 to 1, 1 to 2).fold(1L) { acc, i ->
        acc * checkTrees(i)
    }

    private fun checkTrees(slope: Pair<Int, Int>): Int {
        var trees = 0
        var location = 0 to 0
        while (location.second < input.size) {
            trees += if (input[location.second][location.first] == '#') 1 else 0
            location += slope
        }
        return trees
    }

    private operator fun Pair<Int, Int>.plus(slope: Pair<Int, Int>) = Pair(
        (first + slope.first) % width,
        second + slope.second
    )
}

package year_2022.day_12

import readInput
import util.toAlphabetPosition

data class Position(
    val row: Int,
    val column: Int
)

data class Heatmap (
    val startingPosition: Position,
    val endingPosition: Position,
    val locations: List<List<Int>>
)

object Day12 {

    /**
     * @return
     */
    fun solutionOne(text: List<String>): Int {
        val startingElevation = 'a'.toAlphabetPosition()
        val endingElevation = 'z'.toAlphabetPosition()

        val heatmap = parseText(
            text = text,
            startingElevation = startingElevation,
            endingElevation = endingElevation
        )

        val path = findPath(heatmap, heatmap.startingPosition, heatmap.endingPosition)!!
        return path.size - 1
    }

    /**
     * @return
     */
    fun solutionTwo(text: List<String>): Int {
        val startingElevation = 'a'.toAlphabetPosition()
        val endingElevation = 'z'.toAlphabetPosition()

        val heatmap = parseText(
                text = text,
                startingElevation = startingElevation,
                endingElevation = endingElevation
        )

        val lowestLocations = mutableListOf<Position>()
        heatmap.locations.forEachIndexed { rowIndex, row ->
            row.forEachIndexed { colIndex, height ->
                if (height == 1) {
                    lowestLocations.add(Position(rowIndex, colIndex))
                }
            }
        }

        return lowestLocations
            .mapNotNull { position ->
                findPath(heatmap, position, heatmap.endingPosition)?.size
            }
            .minByOrNull { it }!! - 1
    }

    private fun parseText(
        text: List<String>,
        startingElevation: Int,
        endingElevation: Int
    ): Heatmap {

        var startingPosition = Position(0, 0)
        var endingPosition = Position(0,0)

        val locations = text.mapIndexed() { row, line ->
            line.mapIndexed { column, letter ->
                when (letter) {
                    'S' -> {
                        startingPosition = Position(row, column)
                        startingElevation
                    }
                    'E' -> {
                        endingPosition = Position(row, column)
                        endingElevation
                    }
                    else -> letter.toAlphabetPosition()
                }
            }
        }

        return Heatmap(
            startingPosition = startingPosition,
            endingPosition = endingPosition,
            locations = locations
        )
    }

    private fun findPath(heatmap: Heatmap, startingPosition: Position, endingPosition: Position): List<Position>? {
        val shortestDistance = mutableMapOf<Position, List<Position>>()
        val toEvaluateDistance = mutableListOf<Pair<List<Position>, Position>>() // path to positionToEvaluate

        val startingElevation = heatmap.locations[startingPosition.row][startingPosition.column]

        shortestDistance[startingPosition] = listOf(startingPosition)
        val potentialSteps = potentialSteps(startingPosition, heatmap.locations)
        potentialSteps.forEach { position ->
            toEvaluateDistance.add(listOf(startingPosition) to position)
        }

        while (toEvaluateDistance.isNotEmpty()) {
            val (path, position) = toEvaluateDistance.removeFirst()
            val fullPath = path + position

            when (val previousShortest = shortestDistance[position]) {
                null -> {
                    shortestDistance[position] = fullPath
                    potentialSteps(position, heatmap.locations).forEach { potentialNextStep ->
                        toEvaluateDistance.add(fullPath to potentialNextStep)
                    }
                }
                else -> {
                    if (fullPath.size < previousShortest.size) {
                        shortestDistance[position] = fullPath
                        potentialSteps(position, heatmap.locations).forEach { potentialNextStep ->
                            toEvaluateDistance.add(fullPath to potentialNextStep)
                        }
                    }
                }
            }
        }

        return shortestDistance[endingPosition]
    }

    private fun potentialSteps(position: Position, locations: List<List<Int>>): List<Position> {
        val elevation = locations[position.row][position.column]
        val possibleOtherPositions = mutableListOf<Position>()

        // up
        if (position.row > 0 && locations[position.row - 1][position.column] <= elevation + 1) {
            possibleOtherPositions.add(Position(position.row - 1, position.column))
        }

        // down
        if (position.row < locations.size - 1 && locations[position.row + 1][position.column] <= elevation + 1) {
            possibleOtherPositions.add(Position(position.row + 1, position.column))
        }

        // left
        if (position.column > 0 && locations[position.row][position.column - 1] <= elevation + 1) {
            possibleOtherPositions.add(Position(position.row, position.column - 1))
        }

        // right
        if (position.column < locations.first().size - 1 && locations[position.row][position.column + 1] <= elevation + 1) {
            possibleOtherPositions.add(Position(position.row, position.column + 1))
        }

        return possibleOtherPositions
    }
}

fun main() {
    val inputText = readInput("year_2022/day_12/Day12.txt")

    val solutionOne = Day12.solutionOne(inputText)
    println("Solution 1: $solutionOne")

    val solutionTwo = Day12.solutionTwo(inputText)
    println("Solution 2: $solutionTwo")
}

package day24_arithmetic_logic_unit

/**
 * At first blush, this looks like a "build an interpreter/VM" sort of problem
 * in the style of Intcode. However, 9^14 numbers to check... This is another
 * "find the optimal path" (like Chiton #15), but the thing you need to find a
 * path through is expressed by the input program's _behavior_, not a map you
 * are provided. The only info you know is that the final Z must be zero, so
 * pruning the "paths" has to be done backwards. You don't necessarily need an
 * interpreter either; manually disassembling to identify the algorithm, and
 * then re-coding in your solution language will work just as well.
 *
 * Part two is just inverting the fitness function: find min vs find max.
 */
fun main() {
    util.solve(51983999947999, ::partOne) // 51993999947999 is too high
    util.solve(11211791111365, ::partTwo)
}

private typealias Round = List<Op>

private fun Round.execute(z: Long, w: Long) =
    Machine().let { m ->
        m.z = z
        m.execute(this, listOf(w))
        m.z
    }

private fun Round.searchIn(targets: Set<Long>): Set<Long> =
    mutableSetOf<Long>().let { next ->
        for (z in -10_000..10_000L)
            for (w in 1..9L)
                if (targets.contains(execute(z, w)))
                    next.add(z)
        next
    }

private typealias Slices = List<Round>

private fun Slices.buildInputZByRound(): Map<Int, Set<Long>> =
    mutableMapOf<Int, Set<Long>>().also { byRound ->
        ((size - 1) downTo 0).fold(setOf(0L)) { targets, r ->
            if (targets.isEmpty()) {
                throw IllegalStateException("no targets for round $r")
            }
            byRound[r] = this[r].searchIn(targets)
            byRound[r]!!
        }
    }

fun partOne(input: String) =
    solve(input, 9 downTo 1L)

fun solve(input: String, digitPriority: Iterable<Long>): Long {
    // break the problem into per-digit rounds
    val m = Machine()
    val slices = m.parse(input)
        .chunked(18)

    // back-track and find all the per-round output Z that might work
    val inputZByRound = slices.buildInputZByRound()

    // go forward and find the largest per-round W that passes the check
    fun walk(r: Int, z: Long, digits: List<Long>): Long {
        if (digits.size == 14) {
            return digits.fold(0L) { agg, n ->
                agg * 10 + n
            }
        }
        val targets = if (r == 13) setOf(0L)
        else inputZByRound[r + 1]!!
        for (w in digitPriority) {
            val v = slices[r].execute(z, w)
            if (targets.contains(v)) {
                val answer = walk(r + 1, v, digits + w)
                if (answer > 0) {
                    return answer
                }
            }
        }
        return -1
    }
    return walk(0, 0, emptyList())
}

fun partTwo(input: String) =
    solve(input, 1..9L)

package com.adventofcode.year2021.day3.part2

import com.adventofcode.year2021.day
import java.math.BigInteger

fun List<String>.mostCommonBit(column: Int): Char {
  return when (count { it[column] == '1' } >= count { it[column] == '0' }) {
    true -> '1'
    else -> '0'
  }
}

fun List<String>.leastCommonBit(column: Int): Char {
  return when (count { it[column] == '0' } <= count { it[column] == '1' }) {
    true -> '0'
    else -> '1'
  }
}

tailrec fun List<String>.filterBy(detectBitCriteria: List<String>.(Int) -> Char, column: Int = 0): BigInteger {
  val bitCriteria = detectBitCriteria(column)
  val foundedNumbers = filter { it[column] == bitCriteria }
  return when (foundedNumbers.size > 1) {
    true -> foundedNumbers.filterBy(detectBitCriteria, column + 1)
    else -> BigInteger(foundedNumbers.first(), 2)
  }
}


fun main() {
  val allBinaryNumbers = day(3)
  val oxygenGeneratorRating = allBinaryNumbers.filterBy(List<String>::mostCommonBit)
  val co2ScrubberRating = allBinaryNumbers.filterBy(List<String>::leastCommonBit)
  println(oxygenGeneratorRating * co2ScrubberRating)
}

package com.github.fantom.codeowners.search

import com.github.fantom.codeowners.CodeownersManager
import com.github.fantom.codeowners.OwnersList
import com.github.fantom.codeowners.indexing.CodeownersEntryOccurrence
import com.github.fantom.codeowners.indexing.OwnerString
import com.intellij.openapi.vfs.VirtualFile

interface Predicate {
    // TODO change to OwnersSet, duplicates make no sense
    fun satisfies(fileOwners: OwnersList?): Boolean
}

sealed interface Filter: Predicate {
    fun displayName(): CharSequence

    // TODO rewrite using service + context
    /**
     * @return false, if this filter definitely cannot be satisfied by given codeowners file, true otherwise
     */
    fun satisfiable(codeownersFile: CodeownersEntryOccurrence): Boolean

    sealed interface Condition: Filter {
        // files without assigned owners
        data object Unowned: Condition {
            override fun displayName() = "unowned"

            override fun satisfiable(codeownersFile: CodeownersEntryOccurrence): Boolean {
                return true // cannot easily calculate it precise: need to proof there is at least one unowned file
            }

            override fun satisfies(fileOwners: OwnersList?) = fileOwners == null
        }

        // explicitly unowned files
        data object OwnershipReset: Condition {
            override fun displayName() = "explicitly unowned"

            override fun satisfiable(codeownersFile: CodeownersEntryOccurrence): Boolean {
                return codeownersFile.items.any { it.second.owners.isEmpty() }
            }

            override fun satisfies(fileOwners: OwnersList?) = fileOwners?.isEmpty() ?: false
        }

        // files, owned by any (at least one) of the given owners
        data class OwnedByAnyOf(val owners: Set<OwnerString>): Condition {
            override fun displayName() = "owned by any of ${owners.joinToString(", ")}"

            override fun satisfiable(codeownersFile: CodeownersEntryOccurrence): Boolean {
                return true // because "owners" can be only from this file, so they own some files
            }

            override fun satisfies(fileOwners: OwnersList?) = fileOwners?.any { it in owners } ?: false
        }

        // files, owned by all the given owners, and maybe by some extra owners
        data class OwnedByAllOf(val owners: Set<OwnerString>): Condition {
            override fun displayName() = "owned by all of ${owners.joinToString(", ")}"

            override fun satisfiable(codeownersFile: CodeownersEntryOccurrence): Boolean {
                return codeownersFile.items.map { it.second }.any { satisfies(it.owners) }
            }

            override fun satisfies(fileOwners: OwnersList?) = fileOwners?.containsAll(owners) ?: false
        }

        // files, owned by only given owners, no extra owners allowed
        data class OwnedByExactly(val owners: Set<OwnerString>): Condition {
            override fun displayName() = "owned by exactly ${owners.joinToString(", ")}"

            override fun satisfiable(codeownersFile: CodeownersEntryOccurrence): Boolean {
                return codeownersFile.items.map { it.second }.any { satisfies(it.owners) }
            }

            override fun satisfies(fileOwners: OwnersList?) = fileOwners?.let { owners == it.toSet() } ?: false
        }
    }

    // negation of some Condition
    data class Not(val condition: Condition): Filter {
        override fun displayName() = "not ${condition.displayName()}"

        override fun satisfiable(codeownersFile: CodeownersEntryOccurrence): Boolean {
            // cannot calculate it in abstract: it depends on the nature of the "condition"
            return true
        }

        override fun satisfies(fileOwners: OwnersList?): Boolean {
            return !condition.satisfies(fileOwners)
        }
    }
}

// disjunction of conjunctions
data class DNF(val filters: List<List<Filter>>): Predicate {
    override fun satisfies(fileOwners: OwnersList?) = filters.any { conj -> conj.all { n -> n.satisfies(fileOwners) } }

    fun displayName() =
        filters.joinToString(" or ") { conj -> "(${conj.joinToString(" and ") { n -> n.displayName() }})" }
}

data class CodeownersSearchFilter(
    val codeownersFile: CodeownersEntryOccurrence,
    // DNF: disjunction of conjunctions
    private val dnf: DNF
): Predicate by dnf {
    fun displayName() = "${codeownersFile.url}: ${dnf.displayName()}"

    context(CodeownersManager)
    fun satisfies(file: VirtualFile): Boolean {
        val ownersRef = getFileOwners(file, codeownersFile).getOrNull() ?: return false
        val ownersList = ownersRef.ref?.owners

        return satisfies(ownersList)
    }
}

fun main() {

    fun findFirstUniqueSequence(input: String, windowSize: Int) : Int {
        var startIndex = 0

        for (i in IntRange(0, input.length-windowSize-1) step 1) {
            val letters = input.substring(i, i+windowSize)
            if (letters.toSet().size == letters.toList().size) {
                startIndex = i
                break
            }
        }
        return startIndex + windowSize
    }

    fun part1(input: List<String>) : List<Int> {
        val windowSize = 4
        return input.map { findFirstUniqueSequence(it, windowSize) }
    }
    fun part2(input: List<String>) : List<Int> {
        val markerSize = 4
        val messageSize = 14
        return input.map { 
            val markerIndex = findFirstUniqueSequence(it, markerSize) + 1
            Pair(
                markerIndex,
                it.substring(markerIndex, it.length)
            )
        }
        .map { findFirstUniqueSequence(it.second, messageSize) + it.first }
    }

    val testInput = readInput("Day06_test")
    check(part1(testInput) == listOf(7, 5, 6, 10, 11))
    check(part2(testInput) == listOf(25,23,23,29,26))

    val input = readInput("Day06")
    check(part1(input) == listOf(1598))
    check(part2(input) == listOf(2414))
}

