                when (it.instruction) {
                    "on" -> light.increase()
                    "off" -> light.decrease()
                    else -> light.jump()
                }
            }
        }
        printMatrix(lightGrid) {
            it.brightness.toString()
        }
        return lightGrid.flatten().sumOf { it.brightness }
    }
}

