import java.lang.IllegalStateException

enum class Shape {
    Rock,
    Paper,
    Scissors
}

fun opponentInputToShape(s: String): Shape =
    when (s) {
        "A" -> Shape.Rock
        "B" -> Shape.Paper
        "C" -> Shape.Scissors
        else -> throw IllegalStateException("invalid opponent input $s")
    }

fun ourInputToShape(s: String): Shape =
    when (s) {
        "X" -> Shape.Rock
        "Y" -> Shape.Paper
        "Z" -> Shape.Scissors
        else -> throw IllegalStateException("invalid our input $s")
    }

fun scoreForShape(shape: Shape) =
    when (shape) {
        Shape.Rock -> 1
        Shape.Paper -> 2
        Shape.Scissors -> 3
    }

fun scoreForRound(me: Shape, opponent: Shape): Int {
    if (me == opponent) {
        return 3 // draw
    }

    return when (me to opponent) {
        (Shape.Rock to Shape.Scissors) -> 6 // win
        (Shape.Scissors to Shape.Paper) -> 6 // win
        (Shape.Paper to Shape.Rock) -> 6 // win
        else -> 0 // lost
    }
}

fun main() {
    fun scoreForTwoShapes(me: Shape, opponent: Shape) =
        scoreForShape(me) + scoreForRound(me, opponent)

    fun part1(input: List<String>): Int =
        input.sumOf {
            val (s, t) = it.split(" ")
            val ourShape = ourInputToShape(t)
            val opponentShape = opponentInputToShape(s)

            scoreForTwoShapes(ourShape, opponentShape)
        }

    fun determineShape(opponent: Shape, expectedOutcome: String) =
        when (opponent to expectedOutcome) {
            Shape.Rock to "Z" -> Shape.Paper
            Shape.Scissors to "Z" -> Shape.Rock
            Shape.Paper to "Z" -> Shape.Scissors
            Shape.Rock to "X" -> Shape.Scissors
            Shape.Scissors to "X" -> Shape.Paper
            Shape.Paper to "X" -> Shape.Rock
            else -> opponent
    }

    fun part2(input: List<String>): Int =
        input.sumOf {
            val (s, t) = it.split(" ")
            val opponentShape = opponentInputToShape(s)
            val ourShape = determineShape(opponentShape, t)

            scoreForTwoShapes(ourShape, opponentShape)
        }

    // test if implementation meets criteria from the description, like:
    val testInput = readInput("Day02_test")
    check(part1(testInput) == 15)

    val input = readInput("Day02")
    println(part1(input))
    println(part2(input))
}

package com.ajithkumar.aoc2022

import com.ajithkumar.utils.*
import kotlin.math.absoluteValue

class Knot(var X: Int, var Y: Int) {
    fun moveAlone(direction: String) {
        when(direction) {
            "L" -> X-=1
            "R" -> X+=1
            "U" -> Y+=1
            "D" -> Y-=1
        }
    }

    fun moveLinked(leadingKnot: Knot): Boolean {
        val xDiff = (leadingKnot.X - this.X)
        val yDiff = (leadingKnot.Y - this.Y)
        if(xDiff.absoluteValue > 1 || yDiff.absoluteValue > 1) {
            if(xDiff.absoluteValue == 2 && yDiff.absoluteValue == 2) {
                X = leadingKnot.X + (if(xDiff < 0)  1 else -1)
                Y = leadingKnot.Y + (if(yDiff < 0)  1 else -1)
            } else if(xDiff.absoluteValue == 2) {
                X = leadingKnot.X + (if(xDiff < 0)  1 else -1)
                Y = if(yDiff.absoluteValue > 0) leadingKnot.Y else this.Y
            } else {
                X = if(xDiff.absoluteValue > 0) leadingKnot.X else this.X
                Y = leadingKnot.Y + (if(yDiff < 0)  1 else -1)
            }
            return true
        }
        return false
    }

    fun currentPosition(): Pair<Int, Int> {
        return Pair(X, Y)
    }
}

class Rope(private val numberOfKnots: Int) {
    private val knots = buildList {
        repeat(numberOfKnots) {
            add(Knot(0,0))
        }
    }
    val tailVisited = mutableSetOf(knots.last().currentPosition())
    fun moveHead(direction: String, steps: Int) {
        repeat(steps) {
            moveHeadOnce(direction)
            tailVisited.add(knots.last().currentPosition())
        }
    }

    private fun moveHeadOnce(direction: String) {
        knots[0].moveAlone(direction)
        for(i in 1 until knots.size) {
            val moved = knots[i].moveLinked(knots[i-1])
            if(!moved) break
        }
    }
}

fun main() {
    fun part1(input: List<String>): Int {
        val knotsInRope = 2

        val moves = input.map { it.split(" ") }
        val rope = Rope(knotsInRope)
        moves.forEach { (dir, steps) ->
            rope.moveHead(dir, steps.toInt())
        }
        return rope.tailVisited.size
    }

    fun part2(input: List<String>): Int {
        val knotsInRope = 10

        val moves = input.map { it.split(" ") }
        val rope = Rope(knotsInRope)
        moves.forEach { (dir, steps) ->
            rope.moveHead(dir, steps.toInt())
        }
        return rope.tailVisited.size
    }

