class Day22CrabCombat(customInput: PuzzleInput? = null) : Puzzle(customInput) {
    private val player1 by lazy { input.split("\n\n").first().lines().drop(1).map(String::toInt) }
    private val player2 by lazy { input.split("\n\n").last().lines().drop(1).map(String::toInt) }

    override fun partOne() = CrabCombatGame(player1.toMutableList(), player2.toMutableList()).playRegularGame()

    override fun partTwo() = CrabCombatGame(player1.toMutableList(), player2.toMutableList()).playRecursiveGame(true)

    companion object {
        private data class CrabCombatGame(
            val player1: MutableList<Int>,
            val player2: MutableList<Int>
        ) {
            private fun computeScore() = (player1 + player2).reversed().mapIndexed { position, card -> card * (position + 1) }.sum()

            fun playRegularGame(): Int {
                while (player1.isNotEmpty() && player2.isNotEmpty()) {
                    val card1 = player1.removeAt(0)
                    val card2 = player2.removeAt(0)

                    if (card1 > card2) {
                        player1.addAll(listOf(card1, card2))
                    } else {
                        player2.addAll(listOf(card2, card1))
                    }
                }

                return computeScore()
            }

            fun playRecursiveGame(startingGame: Boolean): Int {
                val previousConfigurations = hashSetOf<CrabCombatGame>()

                while (player1.isNotEmpty() && player2.isNotEmpty()) {
                    if (previousConfigurations.contains(this)) {
                        return 1
                    }

                    previousConfigurations.add(this)

                    val card1 = player1.removeAt(0)
                    val card2 = player2.removeAt(0)

                    val winningPlayer = when {
                        player1.size >= card1 && player2.size >= card2 ->
                            CrabCombatGame(
                                player1.take(card1).toMutableList(),
                                player2.take(card2).toMutableList()
                            ).playRecursiveGame(false)
                        card1 > card2 -> 1
                        else -> 2
                    }

                    if (winningPlayer == 1) {
                        player1.addAll(listOf(card1, card2))
                    } else {
                        player2.addAll(listOf(card2, card1))
                    }
                }

                return when (startingGame) {
                    true -> computeScore()
                    false -> if (player1.size > player2.size) 1 else 2
                }
            }
        }
    }
}

class Day05 : Challenge<String> {
    override val name: String
        get() = "Day 05"

    override fun inputName(): String = "Day05"

    override fun testInputName(): String = "Day05_test"

    override fun testResult1(): String = "CMZ"

    override fun testResult2(): String = "MCD"

    override fun part1(input: String): String {
        val challengeSetup = input.split("\n\n")

        val stacks = challengeSetup.first().split("\n").parseStacks()

        challengeSetup[1]
            .split("\n")
            .filter { it.isNotBlank() }
            .map { it.moves() }
            .forEach { move -> stacks.applyMove9000(move) }

        return stacks.evaluateChallenge()
    }

    override fun part2(input: String): String {
        val challengeSetup = input.split("\n\n")

        val stacks = challengeSetup.first().split("\n").parseStacks()

        challengeSetup[1]
            .split("\n")
            .filter { it.isNotBlank() }
            .map { it.moves() }
            .forEach { move -> stacks.applyMove9001(move) }

        return stacks.evaluateChallenge()
    }

    private fun Map<Int, MutableList<Char>>.applyMove9001(move: Triple<Int, Int, Int>) {
        val (amount, from, to) = move

        this[to]?.addAll(
            this[from]?.takeLast(amount)
                ?: throw IndexOutOfBoundsException("Cannot move $amount crates from $from to $to of $this")
        )
        repeat((1..amount).count()) { this[from]?.removeLastOrNull() }
    }
    private fun Map<Int, MutableList<Char>>.applyMove9000(move: Triple<Int, Int, Int>) {
        val (amount, from, to) = move

        this[to]?.addAll(
            this[from]?.takeLast(amount)?.reversed()
                ?: throw IndexOutOfBoundsException("Cannot move $amount crates from $from to $to of $this")
        )
        repeat((1..amount).count()) { this[from]?.removeLastOrNull() }
    }

    private fun Map<Int, List<Char>>.evaluateChallenge(): String =
        toSortedMap().mapNotNull { it.value.lastOrNull { c -> c.isLetter() } }.joinToString("")


    private fun String.moves(): Triple<Int, Int, Int> {
        val parts = split(" ")

        return Triple(parts[1].toInt(), parts[3].toInt(), parts[5].toInt())
    }

    private fun List<String>.parseStacks(): Map<Int, MutableList<Char>> {
        val numberOfStacks = last().split(regex = Regex("\\s")).last { it.isNotBlank() }.toInt()
        val result = (1..numberOfStacks).associateWith { mutableListOf<Char>() }

        reversed().drop(1).forEach {
            it.crates(numberOfStacks).forEachIndexed { index, c ->
                if (c.isLetter()) {
                    result[index + 1]?.add(c)
                        ?: throw IndexOutOfBoundsException("Failed to insert $c at index $index into $result")

                }
            }
        }

        return result
    }

    private fun String.crates(numberOfStacks: Int): List<Char> {
        return substring(1).mapIndexedNotNull { index, c ->
            if (index % 4 == 0) {
                c
            } else {
                null
            }
        }
    }
}

package algorithms

/**
 * I pick a number from 1 to n. You have to guess which number I picked. Every time you guess wrong, I'll tell you whether the number is higher or lower.
 * You call a pre-defined API guess(int num) which returns 3 possible results (-1, 1, or 0):
 */
internal data class NumberGuessingGame(val luckyNumber: Int) {
    /**
     * @return 0 if the given number is equal to [luckyNumber]; -1 if [luckyNumber] is lower than the given number;
     * 1 if [luckyNumber] is higher than the given number.
     */
    private fun guess(guessedNumber: Int): Int {
        return when {
            guessedNumber == luckyNumber -> 0
            guessedNumber > luckyNumber -> -1
            else -> 1
        }
    }

    /**
     * Guesses the lucky number using binary search and the given maximum number. The limit for guessing is 1 to [maxNumber] (inclusive).
     * @return the guessed number.
     */
    fun guessNumber(maxNumber: Int): Int {
        //Binary search approach
        var right = maxNumber
        var left = 1
        var pivot: Int
        while (left <= right) {
            pivot = left + (right - left) / 2
            val guessResult = guess(pivot)
            when (guessResult) {
                0 -> //We're done
                    return pivot
                -1 -> //The target number is lower than the current pivot
                    right = pivot
                1 -> //The target number is higher than the current pivot
                    left = pivot + 1
            }
        }
        //This constitutes an error
        return -1
    }
}

