import java.util.Stack

fun main() {
    fun getInitialStacks(input: List<String>): List<ArrayDeque<Char>> {
        val filteredInput = input.map { it.toList().filterIndexed { index, _ -> index % 4 == 1 } }
        val stacks: List<ArrayDeque<Char>> = List(filteredInput[0].size) { ArrayDeque() }
        for (indexOfLine in filteredInput.lastIndex - 1 downTo 0) {
            for (indexOfStack in filteredInput[indexOfLine].indices) {
                if (filteredInput[indexOfLine][indexOfStack] != ' ') stacks[indexOfStack].add(filteredInput[indexOfLine][indexOfStack])
            }
        }
        return stacks
    }

    fun makeOneMoveOldVersion(howMany: Int, stackFromIndex: Int, stackToIndex: Int, stacks: List<ArrayDeque<Char>>) {
        for (i in 1..howMany) {
            stacks[stackToIndex].addLast(stacks[stackFromIndex].removeLastOrNull()!!)
        }
    }

    fun makeOneMoveNewVersion(howMany: Int, stackFromIndex: Int, stackToIndex: Int, stacks: List<ArrayDeque<Char>>) {
        val optionalStack: ArrayDeque<Char> = ArrayDeque()
        for (i in 1..howMany) {
            optionalStack.addLast(stacks[stackFromIndex].removeLastOrNull()!!)
        }
        for (i in 1..howMany) {
            stacks[stackToIndex].addLast(optionalStack.removeLastOrNull()!!)
        }
    }

    fun getAnswer(stacks: List<ArrayDeque<Char>>): String {
        val answer = mutableListOf<Char>()
        for (stack in stacks) {
            answer.add(stack.last())
        }
        return answer.joinToString(separator = "")
    }

    fun splitInput(realInput: List<String>): Pair<List<String>, List<String>> {
        var separator = 0
        val groupedInput = realInput.groupBy {
                if (it == "") separator += 1
                separator
            }
            .values.toList()
        return Pair(groupedInput[0], groupedInput[1].slice(1..groupedInput[1].lastIndex))
    }

    fun part1(stackInput: List<String>, moveInput: List<String>): String {
        val stacks: List<ArrayDeque<Char>> = getInitialStacks(stackInput)
        moveInput.forEach {
            val args = it.split(" ")
            makeOneMoveOldVersion(Integer.parseInt(args[1]), Integer.parseInt(args[3]) - 1, Integer.parseInt(args[5]) - 1, stacks)
        }
        return getAnswer(stacks)
    }

    fun part2(stackInput: List<String>, moveInput: List<String>): String {
        val stacks: List<ArrayDeque<Char>> = getInitialStacks(stackInput)
        moveInput.forEach {
            val args = it.split(" ")
            makeOneMoveNewVersion(Integer.parseInt(args[1]), Integer.parseInt(args[3]) - 1, Integer.parseInt(args[5]) - 1, stacks)
        }
        return getAnswer(stacks)
    }

    val (stackInput, moveInput) = splitInput(readInput("Day05"))
    println(part1(stackInput, moveInput))
    println(part2(stackInput, moveInput))
}

package g2301_2400.s2392_build_a_matrix_with_conditions

// #Hard #Array #Matrix #Graph #Topological_Sort
// #2023_07_02_Time_706_ms_(100.00%)_Space_65.8_MB_(100.00%)

import java.util.LinkedList
import java.util.Queue

class Solution {
    // Using topological sort to solve this problem
    fun buildMatrix(k: Int, rowConditions: Array<IntArray>, colConditions: Array<IntArray>): Array<IntArray> {
        // First, get the topo-sorted of row and col
        val row = toposort(k, rowConditions)
        val col = toposort(k, colConditions)
        // base case: when the length of row or col is less than k, return empty.
        // That is: there is a loop in established graph
        if (row.size < k || col.size < k) {
            return Array(0) { IntArray(0) }
        }
        val res = Array(k) { IntArray(k) }
        val map: MutableMap<Int, Int> = HashMap()
        for (i in 0 until k) {
            // we record the number corresbonding to each column:
            // [number, column index]
            map[col[i]] = i
        }
        // col: 3 2 1
        // row: 1 3 2
        for (i in 0 until k) {
            // For each row: we have number row.get(i). And we need to know
            // which column we need to assign, which is from map.get(row.get(i))
            // known by map.get()
            res[i][map[row[i]]!!] = row[i]
        }
        return res
    }

    private fun toposort(k: Int, matrix: Array<IntArray>): List<Int> {
        // need a int[] to record the indegree of each number [1, k]
        val deg = IntArray(k + 1)
        // need a list to record the order of each number, then return this list
        val res: MutableList<Int> = ArrayList()
        // need a 2-D list to be the graph, and fill the graph
        val graph: MutableList<MutableList<Int>> = ArrayList()
        for (i in 0 until k) {
            graph.add(ArrayList())
        }
        // need a queue to do the BFS
        val queue: Queue<Int> = LinkedList()
        // First, we need to establish the graph, following the given matrix
        for (a in matrix) {
            val from = a[0]
            val to = a[1]
            graph[from - 1].add(to)
            deg[to]++
        }
        // Second, after building a graph, we start the bfs,
        // that is, traverse the node with 0 degree
        for (i in 1..k) {
            if (deg[i] == 0) {
                queue.offer(i)
                res.add(i)
            }
        }
        // Third, start the topo sort
        while (queue.isNotEmpty()) {
            val node = queue.poll()
            val list: List<Int> = graph[node - 1]
            for (i in list) {
                if (--deg[i] == 0) {
                    queue.offer(i)
                    res.add(i)
                }
            }
        }
        return res
    }
}

