class Day07() {
    sealed interface Node {
        val size: Int
        val subNodes: List<Node>
    }

    data class FileNode(override val size: Int, val name: String) : Node {
        override val subNodes: List<Node>
            get() = emptyList()
    }

    data class DirNode(val name: String, override val subNodes: List<Node> = emptyList()) : Node {
        override val size: Int
            get() = subNodes.sumOf { it.size }
    }

    val fileEntry = seq(number() followedBy " ", regex("[a-zA-Z./]+") followedBy "\n", ::FileNode)
    val dirEntry = "dir " followedBy regex("[a-zA-Z./]+") followedBy "\n" asValue null
    val fileDirEntry = dirEntry or fileEntry
    val entry: Parser<Node?> = ref(::dirListing) or fileDirEntry
    val entries = oneOrMore(entry) map { it.filterNotNull() }
    val dirListing = seq(
        "\$ cd " followedBy regex("[a-zA-Z/]+"),
        "\n\$ ls\n" followedBy entries, ::DirNode) followedBy (eoF() or literal("\$ cd ..\n"))


    fun part1(input: String): Int {
        val tree = dirListing.parse(input)
        val r = foldTree(tree, Node::subNodes, 0) { acc, node ->
            acc + if (node is DirNode && node.size < 100000) node.size else 0
        }
        return r
    }

    fun part2(input: String): Int {
        val minimum = 30000000 - 21618835
        val tree = dirListing.parse(input)
        val r = foldTree(tree, Node::subNodes, null) { acc: Node?, node: Node ->
            if (node is DirNode && node.size >= minimum && node.size < (acc?.size ?: Int.MAX_VALUE)) node else acc
        }
        return r?.size ?: 0
    }
}

package com.hj.leetcode.kotlin.problem1572

/**
 * LeetCode page: [1572. Matrix Diagonal Sum](https://leetcode.com/problems/matrix-diagonal-sum/);
 */
class Solution {
    /* Complexity:
     * Time O(N) and Space O(1) where N is the number of rows(columns) of mat;
     */
    fun diagonalSum(mat: Array<IntArray>): Int {
        return sumPrimaryDiagonal(mat) + sumSecondaryDiagonal(mat) - sharedCenterValue(mat)
    }

    private fun sumPrimaryDiagonal(mat: Array<IntArray>): Int {
        var sum = 0
        for (index in mat.indices) {
            sum += mat[index][index]
        }
        return sum
    }

    private fun sumSecondaryDiagonal(mat: Array<IntArray>): Int {
        var sum = 0
        for (index in mat.indices) {
            sum += mat[mat.lastIndex - index][index]
        }
        return sum
    }

    private fun sharedCenterValue(mat: Array<IntArray>): Int {
        val noSharedElement = mat.size.isEven()
        if (noSharedElement) return 0

        val center = mat.size / 2
        return mat[center][center]
    }

    private fun Int.isEven(): Boolean = this and 1 == 0
}

sealed class Instruction(val cycles: Int, val operation: (previousX: Int) -> Int) {
    class NoOp : Instruction(1, { it })
    class AddX(private val amount: Int) : Instruction(2, { it + amount })
    companion object {
        fun parse(line: String) = when {
            line == "noop"          -> NoOp()
            line.startsWith("addx") -> AddX(line.substringAfter(" ").toInt())
            else                    -> throw IllegalArgumentException()
        }
    }
}

class CPU {
    val history: MutableList<Int> = mutableListOf(1)
    private val x: Int get() = history.last()
    private fun tick(value: Int = x) = history.add(value)

    fun execute(program: List<Instruction>) = program.forEach { instruction ->
        repeat(instruction.cycles - 1) { tick() }
        tick(instruction.operation(x))
    }
}

class Sprite(width: Int, position: Int) {
    private val reach = (width - 1) / 2
    val occupiedPixels: IntRange = (position - reach)..(position + reach)
}

class CRT(private val width: Int, height: Int) {
    private val pixels: MutableList<Boolean> = MutableList((width * height) + 1) { false }

    fun tick(cycle: Int, sprite: Sprite) {
        val renderingAt = cycle.rem(width)
        pixels[cycle] = renderingAt in sprite.occupiedPixels
    }

    fun render(on: String = "#", off: String = ".") =
        pixels.joinToString("") { if (it) on else off }.chunked(40).forEach(::println)
}

fun main() {
    fun part1(instructions: List<String>): Int {
        val cpu = CPU().apply { execute(instructions.map(Instruction::parse)) }
        return listOf(20, 60, 100, 140, 180, 220).sumOf { cpu.history[it - 1] * it }
    }

    fun part2(instructions: List<String>) {
        val crt = CRT(40, 6)

        CPU()
            .apply { execute(instructions.map(Instruction::parse)) }
            .history
            .forEachIndexed { cycle, x -> crt.tick(cycle, Sprite(3, x)) }

        crt.render()
    }

    val testInput = readInput("Day10_test")
    println(part1(testInput))
    println(part2(testInput))

    val input = readInput("Day10")
    println(part1(input))
    println(part2(input))
}

package day14

class CountProjection {
    fun perform(steps: Int, inputs: List<String>): Long {
        val instructions = extractInstructionMap(inputs)
        val polyCount = initialPolyPairCount(inputs.first())
        val polyTail = inputs.first().last().toString()
        val countForChars = growPolyCountBySteps(steps, polyCount, instructions, polyTail)
        val counts = countForChars.values.sorted()
        return counts.maxOf { it } - counts.minOf { it }
    }

    private tailrec fun growPolyCountBySteps(
        steps: Int,
        polyCount: Map<String, Long>,
        instructions: Map<String, String>,
        tail: String
    ): Map<String, Long> = if (steps == 0) {
        polyCount.keys.fold(mapOf(tail to 1)) { acc, item ->
            val first = item.first().toString()
            val count = polyCount[item]!!
            acc + (first to (acc[first] ?: 0L) + count)
        }
    } else {
        val new = growPolyCountByStep(polyCount, instructions)
        growPolyCountBySteps(steps - 1, new, instructions, tail)
    }

    private fun growPolyCountByStep(
        polyCount: Map<String, Long>,
        instructions: Map<String, String>
    ): Map<String, Long> {
        val updated = mutableMapOf<String, Long>()
        polyCount.forEach { item ->
            val insertion = instructions[item.key]
            if (insertion != null) {
                val keyOne = "${item.key[0]}${insertion}"
                val keyTwo = "${insertion}${item.key[1]}"
                val itemCount = item.value
                updated.addToValue(keyOne, itemCount)
                updated.addToValue(keyTwo, itemCount)
            }
        }
        return updated.filterValues { it > 0L }
    }

    private fun MutableMap<String, Long>.addToValue(key: String, itemCount: Long) {
        this += (key to ((this[key] ?: 0) + itemCount))
    }

    private fun initialPolyPairCount(input: String): Map<String, Long> =
        input.windowed(size = 2, partialWindows = true).fold(emptyMap()) { acc, item ->
            acc + (item to (acc[item] ?: 0L) + 1L)
        }

    private fun extractInstructionMap(inputs: List<String>): Map<String, String> =
        inputs.subList(2, inputs.size).associate {
            val (target, replace) = it.split(" -> ")
            target to replace
        }
}

