package dev.shtanko.algorithms.leetcode

/**
 * 1125. Smallest Sufficient Team
 * @see <a href="https://leetcode.com/problems/smallest-sufficient-team/">Source</a>
 */
fun interface SmallestSufficientTeam {
    operator fun invoke(reqSkills: Array<String>, people: List<List<String>>): IntArray
}

class SmallestSufficientTeamDFS : SmallestSufficientTeam {
    private var sol: MutableList<Int> = ArrayList()

    override operator fun invoke(reqSkills: Array<String>, people: List<List<String>>): IntArray {
        val idx: MutableMap<String, Int> = HashMap()
        var n = 0
        for (s in reqSkills) idx[s] = n++ // skills are represented by 0, 1, 2....

        val pe = IntArray(people.size)
        for (i in pe.indices) {
            for (p in people[i]) {
                val skill = idx[p]!!
                pe[i] += 1 shl skill
            }
        } // each person is transferred to a number, of which the bits of 1 means the guy has the skill

        search(0, pe, ArrayList(), n)
        val ans = IntArray(sol.size)
        for (i in sol.indices) ans[i] = sol[i]
        return ans
    }

    fun search(cur: Int, pe: IntArray, onesol: MutableList<Int>, n: Int) {
        // when all bits are 1, all skills are covered
        if (cur == (1 shl n) - 1) {
            if (sol.isEmpty() || onesol.size < sol.size) {
                sol = ArrayList(onesol)
            }
            return
        }
        if (sol.isNotEmpty() && onesol.size >= sol.size) {
            return
        }
        var zeroBit = 0
        while (cur shr zeroBit and 1 == 1) zeroBit++
        for (i in pe.indices) {
            val per = pe[i]
            if (per shr zeroBit and 1 == 1) {
                onesol.add(i) // when a person can cover a zero bit in the current number, we can add him
                search(cur or per, pe, onesol, n)
                onesol.removeAt(onesol.size - 1) // search in a backtracking way
            }
        }
    }
}

import Shape.*
import Result.*
import java.lang.IllegalArgumentException

enum class Shape(val points: Int) { ROCK(1), PAPER(2), SCISSORS(3) }
enum class Result(val points: Int) { WIN(6), DRAW(3), LOOSE(0) }


fun main() {

    fun mapToShape(shape: String): Shape {
        return when (shape) {
            "X", "A" -> ROCK
            "Y", "B" -> PAPER
            "Z", "C" -> SCISSORS
            else -> {
                throw IllegalArgumentException("Could not convert $shape to a shape")
            }
        }
    }

    fun mapToResult(result: String): Result {
        return when (result) {
            "X" -> LOOSE
            "Y" -> DRAW
            "Z" -> WIN
            else -> {
                throw IllegalArgumentException("Could not convert $result to a result")
            }
        }
    }

    fun calculateResult(theirHand: Shape, myHand: Shape): Result {

        if (theirHand == myHand) {
            return DRAW
        }

        return when (myHand) {
            ROCK -> if (theirHand == SCISSORS) WIN else LOOSE
            PAPER -> if (theirHand == ROCK) WIN else LOOSE
            SCISSORS -> if (theirHand == PAPER) WIN else LOOSE
        }

    }

    fun calculate(play: Pair<Shape, Shape>): Int {
        val (theirGesture, myGesture) = play
        val result = calculateResult(theirGesture, myGesture)

        return myGesture.points + result.points

    }




    fun getShapeToMatchResult(strategy: Pair<Shape, Result>): Pair<Shape, Shape> {

        val (theirShape, expectedResult) = strategy
        val myShape = Shape.values().find { calculateResult(theirShape, it) == expectedResult }!!

        return theirShape to myShape

    }

    fun part1(input: List<String>): Int {

        return input
            .map { it.split(" ") }
            .map { mapToShape(it[0]) to mapToShape(it[1]) }
            .sumOf { calculate(it) }

    }

    fun part2(input: List<String>): Int {
        return input
            .map { it.split(" ") }
            .map { mapToShape(it[0]) to mapToResult(it[1]) }
            .map { getShapeToMatchResult(it) }
            .sumOf { calculate(it) }

    }

    fun runTest(expected: Int, day: String, testFunction: (List<String>) -> Int) {
        val actual = testFunction(readInput("Day${day}_test"))
        check(expected == actual)
        {
            "Failing for day $day, ${testFunction}. " +
                    "Expected $expected but got $actual"
        }
    }


    val day = "02"

    // test if implementation meets criteria from the description, like:
    runTest(15, day, ::part1)
    runTest(12, day, ::part2)


    val input = readInput("Day$day")
    println(part1(input))
    println(part2(input))
}

package leetcode_study_badge.algorithm

class Day2 {
    fun sortedSquares(nums: IntArray): IntArray {
        var right = nums.size - 1
        var left = 0
        val resultArray = IntArray(nums.size)
        var insertPosition = nums.size - 1

        while (left < right) {
            val leftValue = nums[left] * nums[left]
            val rightValue = nums[right] * nums[right]
            if (leftValue > rightValue) {
                resultArray[insertPosition] = leftValue
                insertPosition--
                left++
            } else if (rightValue > leftValue) {
                resultArray[insertPosition] = rightValue
                insertPosition--
                right--
            } else if (rightValue == leftValue) {
                resultArray[insertPosition] = rightValue
                insertPosition--
                resultArray[insertPosition] = leftValue
                insertPosition--
                right--
                left++
            }
        }
        println(resultArray.contentToString())
        if (left == right) {
            resultArray[insertPosition] = nums[left] * nums[left]
        }

        return resultArray
    }

    fun rotate(nums: IntArray, k: Int) {
        for (i in 0 until k) {
            var temp = nums[0]
            nums[0] = nums[nums.size - 1]
            for (a in 1 until nums.size) {
                var swap = nums[a]
                nums[a] = temp
                temp = swap
            }
        }
    }

    fun rotateTwoPointer(nums: IntArray, k: Int) {

        fun reverse(i: Int, j: Int) {
            var i = i; var j = j

            while (i < j) {
                nums[i] = nums[j].also { nums[j] = nums[i] }
                i++; j--
            }
        }

        val N = nums.size
        val k = k % N

        reverse(0, N - 1)
        reverse(0, k - 1)
        reverse(k, N - 1)
    }



}

fun main() {
    println(Day2().sortedSquares(intArrayOf(-7, -3, 2, 3, 11)).contentToString())
    val rotateArray = intArrayOf(1, 2, 3, 4, 5, 6, 7)
    println(rotateArray.contentToString())
    Day2().rotateTwoPointer(rotateArray, 3)
    println(rotateArray.contentToString())
}

class Solution {
    fun distinctNames(ideas: Array<String>): Long {

        val firstToSuffix = Array(26) { hashSetOf<String>() }
        var res = 0L

        ideas.forEach { 
            firstToSuffix[it[0] - 'a'].add(it.substring(1, it.length))
        }

        for (i in 0 until 26) {
            for (j in i until 26) {
                val common = firstToSuffix[i].intersect(firstToSuffix[j]).size
                val mapI = firstToSuffix[i].size - common
                val mapJ = firstToSuffix[j].size - common
                res += (mapI * mapJ) * 2
            }
        }

        return res
    }
}

// without using kotlins intersect() funtions
class Solution {
    fun distinctNames(ideas: Array<String>): Long {

        val firstToSuffix = Array(26) { hashSetOf<String>() }
        var res = 0L

        ideas.forEach { 
            firstToSuffix[it[0] - 'a'].add(it.substring(1, it.length))
        }

        for (i in 0 until 26) {
            for (j in i until 26) {

                var common = 0
                for(wordA in firstToSuffix[i]) {
                    if(wordA in firstToSuffix[j]) common++
                }
                val mapI = firstToSuffix[i].size - common
                val mapJ = firstToSuffix[j].size - common
                res += (mapI * mapJ) * 2
            }
        }

        return res
    }
}

