import Puzzle
import PuzzleInput
import java.io.File
import kotlin.math.abs

fun day9Puzzle() {

    Day9PuzzleSolution().solve(Day9PuzzleInput("inputs/day9/example.txt", 2, 13))
    Day9PuzzleSolution().solve(Day9PuzzleInput("inputs/day9/input.txt", 2, 6271))
    Day9PuzzleSolution().solve(Day9PuzzleInput("inputs/day9/example.txt", 10, 1))
    Day9PuzzleSolution().solve(Day9PuzzleInput("inputs/day9/example2.txt", 10, 36))
    Day9PuzzleSolution().solve(Day9PuzzleInput("inputs/day9/input.txt", 10, 2458))
}

class Day9PuzzleInput(val input: String, numKnots: Int, expectedResult: Int? = null) : PuzzleInput<Int>(expectedResult) {
    val moves = File(input).readLines()
    val pointsVisited = HashMap<Pair<Int, Int>, Boolean>()
    private val knots = Array(numKnots) {
        Pair(0, 0)
    }

    private fun getKnot(head: Pair<Int, Int>, tail: Pair<Int, Int>): Pair<Int, Int> {
        val x = head.first - tail.first
        val y = head.second - tail.second
        if(abs(x) > 1 || abs(y) > 1) {
            return Pair(tail.first + x.coerceIn(-1, 1), tail.second + y.coerceIn(-1, 1))
        }
        return tail
    }

    private fun moveHead(newHead: Pair<Int, Int>) {
        knots[0] = newHead
        for(i in 1 until knots.size) {
            knots[i] = getKnot(knots[i-1], knots[i])
        }
        pointsVisited[knots.last()] = true
    }
    fun moveHead(xOffset: Int, yOffset: Int, amount: Int) {
        for(move in 1 .. amount) {
            moveHead(Pair(knots[0].first + xOffset, knots[0].second + yOffset))
        }
    }
}

class Day9PuzzleSolution : Puzzle<Int, Day9PuzzleInput>() {
    override fun solution(input: Day9PuzzleInput): Int {
        input.pointsVisited[Pair(0, 0)] = true
        input.moves.forEach { move ->
            val moveParts = move.split(" ")
            val direction = moveParts[0]
            val amount = moveParts[1].toInt()
            when(direction) {
                "U" -> input.moveHead(0, 1, amount)
                "D" -> input.moveHead(0, -1, amount)
                "L" -> input.moveHead(-1, 0, amount)
                "R" -> input.moveHead(1, 0, amount)
            }
        }

        return input.pointsVisited.size
    }

}

package com.hopkins.aoc.day21

import com.hopkins.aoc.day17.Point
import java.io.File

const val debug = true
const val part = 1

/** Advent of Code 2023: Day 21 */
fun main() {
    // Step 1: Read the file input
    val lines: List<String> = File("input/input21-ex1.txt").readLines()
    if (debug) {
        println("Step 1: Read file")
        println("=======")
        println("  num lines: ${lines.size}")
    }

    // Step 2: Build the map
    var startPoint: Point = Point.of(-1, -1)
    val mapHeight = lines.size
    val mapWidth = lines[0].length
    val mapSize = Point.of(mapWidth, mapHeight)
    val rocks: Set<Point> = lines.flatMapIndexed {y, line ->
        line.mapIndexedNotNull { x, c ->
            if (c == '#') {
                Point.of(x, y)
            } else {
                if (c == 'S') {
                    startPoint = Point.of(x, y)
                }
                null
            }
        }
    }.toSet()
    require(startPoint.x >= 0)
    require(startPoint.y >= 0)

    if (debug) {
        println("Step 2: Build the map")
        println("=======")
        println("  map size: $mapSize")
        println("  start: $startPoint")
        println("  num rocks: ${rocks.size}")
    }

    var current = setOf(startPoint)
    for (i in 1..50) {
        val next =
            current.flatMap { point ->
                directions.map { direction ->
                    point.add(direction) }}
                .map { point ->
                    if (point.x == -1) {
                        Point.of(point.x + mapWidth, point.y)
                    } else if (point.y == -1) {
                        Point.of(point.x, point.y + mapWidth)
                    } else if (point.x == mapWidth) {
                        Point.of(0, point.y)
                    } else if (point.y == mapHeight) {
                        Point.of(point.x, 0)
                    } else {
                        point
                    }
                }
                .filterNot { point -> rocks.contains(point) }
                .toSet()
        println("Step $i:")
        printMap(mapSize, rocks, next)
        current = next
    }
    println("Num Plots: ${current.size}")
}

fun printMap(mapSize: Point, rocks: Set<Point>, current: Set<Point>) {
    for (y in 0 until mapSize.y) {
        for (x in 0 until mapSize.x) {
            val point = Point.of(x, y)
            if (rocks.contains(point)) {
                print("#")
            } else if (current.contains(point)) {
                print("O")
            } else {
                print(".")
            }
        }
