import utils.Stack
import utils.execFileByLineIndexed
import utils.numberOfLinesPerFile
import utils.stackOf

class Day13 {

    fun solveFirst(): Int {
        // could have used kotlin.collections.ArrayDeque instead of my own Stack implementation :/
        // with:
        // Stack.push = addLast()
        // Stack.pop = removeLast()
        val (patternRows, patternCols) = createInput()

        var rowSolution = 0
        var colSolution = 0

        patternRows.forEach { rowPattern ->
            var stackRow = Stack<String>()
            var searchIndexRow = -1
            var possibleFindRow: Int? = null
            var isPushingRow = true
            for (p in rowPattern) {
                searchIndexRow++
                if (stackRow.peek() == p) {
                    stackRow.pop()
                    if (possibleFindRow == null) {
                        possibleFindRow = searchIndexRow
                    }
                    isPushingRow = false
                    if (stackRow.isEmpty) {
                        searchIndexRow = possibleFindRow
                        break
                    }
                } else {
                    if (!isPushingRow) {
                        stackRow = stackOf()
                        possibleFindRow = null
                    }
                    stackRow.push(p)
                    isPushingRow = true
                }
            }
            if (!isPushingRow) {
                rowSolution += (possibleFindRow ?: searchIndexRow) * 100
            }
        }

        patternCols.forEach { patternCol ->
            var stackCol = Stack<String>()
            var searchIndexCol = -1
            var possibleFindCol: Int? = null
            var isPushingCol = true
            for (p in patternCol) {
                searchIndexCol++
                if (stackCol.peek() == p) {
                    stackCol.pop()
                    if (possibleFindCol == null) {
                        possibleFindCol = searchIndexCol
                    }
                    isPushingCol = false
                    if (stackCol.isEmpty) {
                        searchIndexCol = possibleFindCol
                        break
                    }
                } else {
                    if (!isPushingCol) {
                        stackCol = stackOf()
                        possibleFindCol = null
                    }
                    stackCol.push(p)
                    isPushingCol = true
                }
            }
            if (!isPushingCol) {
                colSolution += possibleFindCol ?: searchIndexCol
            }
        }
        return rowSolution + colSolution
    }

    fun solveSecond(): Int {
        val (patternRows, patternCols) = createInput()
        var rowSolution = 0
        var colSolution = 0

        patternRows.forEach { rowPattern ->
            rowSolution += findMirror(rowPattern) * 100
        }

        patternCols.forEach { colPattern ->
            colSolution += findMirror(colPattern)
        }

        return rowSolution + colSolution
    }

    private fun findMirror(pattern: List<String>): Int {
        val maxNumber = pattern.size / 2 + 1

        // from top to bottom
        for (i in 1..maxNumber) {
            val arr1 = pattern.take(i)
            val endIndex = if (2 * i >= pattern.size - 1) {
                pattern.size - 1
            } else {
                2 * i
            }
            val arr2 = pattern.subList(i, endIndex).reversed()
            if (diffBy(arr1, arr2) == 1) {
                return i
            }
        }

        for (i in 1..maxNumber) {
            val arr1 = pattern.reversed().take(i)
            val endIndex = if (2 * i >= pattern.size - 1) {
                pattern.size - 1
            } else {
                2 * i
            }
            val arr2 = pattern.reversed().subList(i, endIndex).reversed()
            if (diffBy(arr1, arr2) == 1) {
                return (pattern.size - i)
            }
        }
        return 0
    }

    private fun diffBy(arr1: List<String>, arr2: List<String>): Int {
        var res = 0
        for (i in 0..arr2.lastIndex) {
            res += unCommonChars(arr1[i], arr2[i])
        }
        return res
    }

    private fun unCommonChars(s1: String, s2: String): Int {
        var count = 0
        for (i in s1.indices) {
            count += if (s1[i] == s2[i]) 1 else 0
        }
        return s1.length - count
    }
}

private fun createInput(): Pair<List<List<String>>, List<List<String>>> {
    val patternRows = mutableListOf<List<String>>()
    val patternCols = mutableListOf<List<String>>()
    val pattern = mutableListOf<String>()
    execFileByLineIndexed(13) { it, index ->
        if (it == "") {
            patternRows.add(pattern.toList())
            pattern.clear()
        } else {
            pattern.add(it)
        }
        if (index >= (numberOfLinesPerFile(13) - 1)) {
            patternRows.add(pattern.toList())
            pattern.clear()
        }
    }
    for (p in patternRows) {
        val tempList = mutableListOf<String>()
        for (letterIndex in 0..p[0].lastIndex) {
            val temp = mutableListOf<Char>()
            for (word in p) {
                temp.add(word[letterIndex])
            }
            tempList.add(String(temp.toCharArray()))
        }
        patternCols.add(tempList)
    }
    return Pair(patternRows, patternCols)
}

package io.github.trustedshops_public.spring_boot_starter_keycloak_path_based_resolver.configuration

import org.keycloak.adapters.KeycloakDeployment
import java.lang.IllegalArgumentException

class MatcherConfiguration(
    private val parent: KeycloakPathContextConfigurationHolder,
    private val antPatterns: Array<out String>
) {
    /**
     * Configure keycloak deployment for the given antPatterns
     *
     * @see org.keycloak.adapters.KeycloakDeployment
     */
    fun useKeycloakDeployment(keycloakDeployment: KeycloakDeployment): KeycloakPathContextConfigurationHolder {
        antPatterns
            .associateWith { keycloakDeployment }
            .forEach { (pattern, deployment) ->
                if (parent.mapping.containsKey(pattern)) {
                    throw IllegalArgumentException("pattern '${pattern}' can not be assigned twice")
                }

                parent.mapping[pattern] = deployment
            }
        parent.mapping.putAll(antPatterns.associateWith { keycloakDeployment })
        return parent
    }
}

/**
 * Configuration allowing to map given ant patterns to keycloak deployments
 */
class KeycloakPathContextConfigurationHolder {
    /**
     * Mapping, sorted by the longest string being the first in the map, so iteration always uses the most specific
     * matcher first
     */
    internal val mapping = sortedMapOf<String, KeycloakDeployment>(compareBy<String> { -it.length }.thenBy { it })

    /**
     * Configure context for given ant path matcher
     *
     * For more information check AntPathMatcher
     * @see org.springframework.util.AntPathMatcher
     */
    fun antMatchers(vararg antPatterns: String): MatcherConfiguration = MatcherConfiguration(this, antPatterns)
}

package io.tree

import io.models.TreeNode
import io.utils.runTests
import java.util.*

// https://leetcode.com/problems/increasing-order-search-tree/
class RearrangeTree {

  fun executeRecursive(input: TreeNode?, acc: TreeNode? = null): TreeNode? = when (input) {
    null -> acc
    else -> {
      executeRecursive(input.right, executeRecursive(input.left, acc)?.also {
        var current = it
        while (current.right != null) current = current.right!!
        current.right = TreeNode(input.`val`)
      } ?: TreeNode(input.`val`))
    }
  }

  fun executeIterative(input: TreeNode?): TreeNode? {
    var root: TreeNode? = null
    var current = input
    var last: TreeNode? = null
    val stack = LinkedList<TreeNode>()
    while (current != null || stack.isNotEmpty()) {
      while (current != null) {
        stack.push(current)
        current = current.left
      }
      if (stack.isNotEmpty()) {
        val item = stack.pop()
        TreeNode(item.`val`).let { newNode ->
          if (last == null) {
            root = newNode
            last = newNode
          } else {
            last!!.right = newNode
            last = newNode
          }
        }
        current = item?.right
      }
    }
    return root
  }
}

fun main() {
  runTests(
      listOf(
          Pair(TreeNode(5,
              left = TreeNode(3,
                  left = TreeNode(2, left = TreeNode(1)),
                  right = TreeNode(4)),
              right = TreeNode(6,
                  right = TreeNode(8, left = TreeNode(7), right = TreeNode(9)))),
              TreeNode(1,
                  right = TreeNode(2,
                      right = TreeNode(3,
                          right = TreeNode(4,
                              right = TreeNode(5,
                                  right = TreeNode(6,
                                      right = TreeNode(7,
                                          right = TreeNode(8,
                                              right = TreeNode(9)))))))))
          )
      )
  ) { (input, value) -> Pair(value, RearrangeTree().executeRecursive(input)) }
}

package twentytwentytwo.day16

import twentytwentytwo.day12.Graph
import readInput

fun main() {
    data class ValveNode(
        val id: String,
        val flowRate: Int,
        val neighbours: MutableSet<Pair<String, Int>>,
    )

    val graph = mutableMapOf<String, ValveNode>()
    var availableMinutes = 30
    val startValve by lazy { graph["AA"]!! }

    fun createGraph(input: List<String>): ValveNode {
        input
            .sortedBy { it.split(" ")[1] }
            .forEach {
                val id = it.split(" ")[1]
                val flowRate = it.split(" ")[4].dropLast(1).split("=")[1].toInt()
                val neighbours =
                    it.split(" to ")[1].split(" ").drop(1).map { it.replace(",", "") }
                        .map { Pair(it, 1) }.toMutableSet()
                val valveNode = ValveNode(id, flowRate, neighbours)
                graph[id] = valveNode
            }

        val weights = mutableMapOf<Pair<String, String>, Int>()
        graph.values.forEach { node ->
            node.neighbours.forEach { neighbour ->
                weights[Pair(node.id, neighbour.first)] = 1
            }
        }

        val dijkstraGraph = Graph(
            vertices = graph.values.map { it.id }.toMutableSet(),
            edges = graph.values.map { it.id }
                .associateWith { graph[it]!!.neighbours.map { it.first }.toSet() }.toMutableMap(),
            weights = weights,
        )

        graph.keys.forEach { id ->
            val currNode = graph[id]!!
            val otherNodes = graph.values.toSet() - currNode.neighbours.map { graph[it.first]!! }
                .toSet() - currNode
            val shortestPathTree = dijkstra(dijkstraGraph, currNode.id)
            otherNodes.forEach { other ->
                currNode.neighbours.removeIf { it.first == other.id }
                if (other.flowRate > 0) {
                    val path = shortestPath(shortestPathTree, currNode.id, other.id)
                    currNode.neighbours.add(Pair(other.id, path.size - 1))
                }
            }
        }

        return graph["AA"]!!
    }

    var maxPressureReleased = 0

    fun findMaxPath(
        releasedPressure: Int,
        position: ValveNode,
        visited: Set<ValveNode>,
        minute: Int,
        elephant: Boolean
    ) {
        maxPressureReleased = maxOf(releasedPressure, maxPressureReleased)

        position.neighbours.filter { graph[it.first]!!.flowRate > 0 }.forEach { (id, distance) ->
            val newMinute = minute + distance + 1
            val candidate = graph[id]!!
            if (newMinute < availableMinutes && candidate !in visited) {
                findMaxPath(
                    releasedPressure = releasedPressure + (availableMinutes - newMinute) * candidate.flowRate,
                    position = candidate,
                    visited = visited + candidate,
                    minute = newMinute,
                    elephant = elephant,
                )
            }
        }
        if (elephant) {
            findMaxPath(
                releasedPressure = releasedPressure,
                position = startValve,
                visited = visited,
                minute = 0,
                elephant = false,
            )
        }
    }

    fun part1(input: List<String>): Int {
        val root = createGraph(input)
        maxPressureReleased = 0
        availableMinutes = 30
        findMaxPath(0, root, emptySet(), 0, false)
        return maxPressureReleased
    }

    fun part2(input: List<String>): Int {
        val root = createGraph(input)
        maxPressureReleased = 0
        availableMinutes = 26
        findMaxPath(0, root, emptySet(), 0, true)
        return maxPressureReleased
    }

    val input = readInput("day16", "Day16_input")
    println(part1(input))
    println(part2(input))
}

private fun <T> dijkstra(graph: Graph<T>, start: T): Map<T, T?> {
    val vertexSet: MutableSet<T> =
        mutableSetOf() // a subset of vertices, for which we know the true distance

    val delta = graph.vertices.associateWith { 100000 }.toMutableMap()
    delta[start] = 0

    val previous: MutableMap<T, T?> = graph.vertices.associateWith { null }.toMutableMap()

    while (vertexSet != graph.vertices) {
        val v: T = delta
            .filter { !vertexSet.contains(it.key) }
            .minBy { it.value }
            .key

        graph.edges.getValue(v).minus(vertexSet).forEach { neighbor ->
            val newPath = delta.getValue(v) + graph.weights.getValue(Pair(v, neighbor))

            if (newPath < delta.getValue(neighbor)) {
                delta[neighbor] = newPath
                previous[neighbor] = v
            }
        }

        vertexSet.add(v)
    }

    return previous.toMap()
}

private fun <T> shortestPath(shortestPathTree: Map<T, T?>, start: T, end: T): List<T> {
    fun pathTo(start: T, end: T): List<T> {
        if (shortestPathTree[end] == null) return listOf(end)
        return listOf(pathTo(start, shortestPathTree[end]!!), listOf(end)).flatten()
    }

    return pathTo(start, end)
}

