import dev.haenara.mailprogramming.solution.Solution

/**
 *  2020. 02. 23
 *
 * M x N     (cost)  ,
 *      (0, 0)   (M-1, N-1)    .
 *        .
 *            .
 * ,  (i, j) (i, j+1)  (i+1, j)   .
 *
 * Input
 * [[4, 7, 1, 6],
 *  [5, 7, 3, 9],
 *  [3, 2, 1, 2],
 *  [7, 1, 6, 3]]
 * cost = 25
 *
 * Output
 * 2 (   4-7-1-3-1-6-3, 4-5-7-3-1-2-3)
 *
 * 
 *
 */

class Solution200223 : Solution<Solution200223.Input, Int>{
    private var count = 0
    private lateinit var map : Array<Array<Int>>
    private var cost = 0

    override fun solution(input : Input) : Int {
        map = input.map
        cost = input.cost
        return goRightOrDown(0, 0)
    }

     private fun goRightOrDown(x : Int, y : Int, costSum : Int = 0) : Int {
         val sum = costSum + map[y][x]
         if (x == map[y].lastIndex && y == map.lastIndex) {
             return if (sum == cost) {
                 1
             } else {
                 0
             }
         } else if (x == map[y].lastIndex) {
             // move down
             return goRightOrDown(x, y + 1, sum)
         } else if (y == map.lastIndex) {
             // move right
             return goRightOrDown(x + 1, y, sum)
         } else {
             // move right and down
             return goRightOrDown(x + 1, y, sum) + goRightOrDown(x, y + 1, sum)
         }
     }

    class Input(val map : Array<Array<Int>>, val cost : Int)
}

package com.manalili.advent

class Day05(val input: String) {
    fun react(polymerInput: String = input): Int {
        var polymer = polymerInput
        var index = 0

        while (true) {
            if (index >= polymer.length - 1) break
            if (polymer[index] same polymer[index + 1]) {
                polymer = polymer.removeRange(index..index + 1)
                if (index != 0) index--
            } else {
                index++
            }
        }

        return polymer.length
    }

    fun fullyReact(): Int{
        val units = input.toCharArray().distinctBy { it.toLowerCase() }

        return units.map { testUnit ->  input.filterNot { testUnit.toLowerCase() == it || testUnit.toUpperCase() == it } }
            .map { react(it) }
            .min()!!
    }


    private infix fun Char.same(other: Char) : Boolean {
        return when {
            this.isLowerCase() -> this.toUpperCase() == other
            this.isUpperCase() -> this.toLowerCase() == other
            else -> false
        }
    }

}

