fun arraysContainSameElements(a: IntArray, b: IntArray): Boolean {
    if (a.size != b.size) return false
    val freqA = a.groupBy { it }.mapValues { it.value.size }
    val freqB = b.groupBy { it }.mapValues { it.value.size }
    return freqA == freqB
}

